import React, { useState } from "react";
import { Link } from "react-router-dom";
import ReactDOM from "react-dom"; // Import ReactDOM for portals
function OrdersPage() {
  // States to manage modal visibility and order data
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [orderData, setOrderData] = useState({
    orderId: "",
    customerName: "",
    platform: "Shopify",
    priority: "High",
    products: [],
  });

  // State for adding a new product to the order
  const [newProduct, setNewProduct] = useState("");

  // State to store the orders
  const [orders, setOrders] = useState([]);

  // Toggle Modal
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  // Handle input changes for order details
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Add product to the list
  const handleAddProduct = () => {
    if (newProduct) {
      setOrderData((prevData) => ({
        ...prevData,
        products: [
          ...prevData.products,
          { description: newProduct, id: Date.now() },
        ],
      }));
      setNewProduct("");
    }
  };

  // Remove product from the list
  const handleRemoveProduct = (productId) => {
    setOrderData((prevData) => ({
      ...prevData,
      products: prevData.products.filter((product) => product.id !== productId),
    }));
  };

  // Submit the order and add to orders list
  const handleSubmitOrder = () => {
    setOrders((prevOrders) => [...prevOrders, orderData]);
    setOrderData({
      orderId: "",
      customerName: "",
      platform: "Shopify",
      priority: "High",
      products: [],
    });
    toggleModal();
  };
  // Modal Component
  const Modal = ({ children }) =>
    ReactDOM.createPortal(
      <div className="fixed inset-0 bg-gray-500 bg-opacity-50 flex justify-center items-center z-50">
        <div className="bg-white p-6 rounded shadow-lg w-96">{children}</div>
      </div>,
      document.body // Render modal outside the main DOM tree
    );

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Top Navigation Bar */}
      <nav className="bg-blue-600 text-white shadow-lg">
        <div className="container mx-auto flex justify-between items-center p-4">
          <h1 className="text-2xl font-bold">Amazonment</h1>
          <ul className="flex space-x-4">
            <li className="hover:underline cursor-pointer">
              <Link to="/">Home</Link>
            </li>
            <li className="hover:underline cursor-pointer">
              <Link to="/Products">Products</Link>
            </li>
            <li className="hover:underline cursor-pointer">
              <Link to="/OrdersPage">Orders</Link>
            </li>
            <li className="hover:underline cursor-pointer">Returns</li>
            <li className="hover:underline cursor-pointer">Tools</li>
          </ul>
          <div className="flex items-center space-x-2">
            <span className="hidden sm:block">Profile</span>
            <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center text-gray-800">
              <i className="fas fa-user"></i>
            </div>
          </div>
        </div>
      </nav>
      {/* Header */}
      <header className="bg-white shadow p-4">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold">Orders</h1>
            <p className="text-gray-600">
              <span className="font-semibold">3 Orders</span> | Updated a few
              seconds ago
            </p>
          </div>
          <div className="flex space-x-2">
            <button className="btn btn-outline">Sync</button>
            <button onClick={toggleModal} className="btn btn-outline">
              Create
            </button>
          </div>
        </div>
      </header>
      {/* Submit Button */}
      <div className="mt-4 flex justify-end space-x-2">
        <button onClick={toggleModal} className="btn btn-text">
          Cancel
        </button>
        <button onClick={handleSubmitOrder} className="btn btn-primary">
          Submit
        </button>
      </div>
      {/* Modal for Creating an Order */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">Create Order</h2>
            <input
              type="text"
              name="orderId"
              value={orderData.orderId}
              onChange={handleInputChange}
              placeholder="Order ID"
              className="input input-bordered w-full mb-2"
            />
            <input
              type="text"
              name="customerName"
              value={orderData.customerName}
              onChange={handleInputChange}
              placeholder="Customer Name"
              className="input input-bordered w-full mb-2"
            />
            <select
              name="platform"
              value={orderData.platform}
              onChange={handleInputChange}
              className="select select-bordered w-full mb-2"
            >
              <option value="Shopify">Shopify</option>
              <option value="Magento">Magento</option>
            </select>
            <select
              name="priority"
              value={orderData.priority}
              onChange={handleInputChange}
              className="select select-bordered w-full mb-2"
            >
              <option value="High">High</option>
              <option value="Medium">Medium</option>
              <option value="Low">Low</option>
            </select>

            {/* Product Addition Section */}
            <div className="space-y-2">
              <input
                type="text"
                value={newProduct}
                onChange={(e) => setNewProduct(e.target.value)}
                placeholder="Enter Product SKU/Description"
                className="input input-bordered w-full mb-2"
              />
              <button onClick={handleAddProduct} className="btn btn-outline">
                Add Product
              </button>
            </div>

            {/* Body */}
            <div className="p-4 space-y-4">
              {/* Search and Filters */}
              <div className="flex items-center space-x-4">
                <input
                  type="text"
                  placeholder="Search Orders"
                  className="input input-bordered w-full"
                />
                <select className="select select-bordered">
                  <option>Order List</option>
                  <option>Recently Added</option>
                </select>
                <select className="select select-bordered">
                  <option>Platform: All</option>
                  <option>Shopify</option>
                </select>
                <select className="select select-bordered">
                  <option>Priority: High</option>
                  <option>Priority: Low</option>
                </select>
              </div>

              {/* Tab Menu */}
              <div className="flex space-x-4 border-b pb-2">
                <button className="tab tab-active border-b-2 border-blue-600">
                  New
                </button>
                <button className="tab">History</button>
              </div>

              {/* Action Buttons */}
              <div className="flex justify-end space-x-2">
                <button className="btn btn-text">Cancel</button>
                <button className="btn btn-primary">Confirm Shipment</button>
              </div>

              {/* Order Cards */}
              <div className="space-y-4">
                {[1, 2, 3].map((orderId) => (
                  <div
                    key={orderId}
                    className="card bg-white shadow p-4 flex justify-between items-start"
                  >
                    <input type="checkbox" />
                    <div>
                      <h3 className="font-semibold">Order ID: #{orderId}</h3>
                      <p className="text-gray-600">
                        John Doe | Shopify |{" "}
                        <span className="text-red-500">High Priority</span>
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            {/* Product List */}
            <div>
              {orderData.products.map((product, i) => (
                <div key={product.id} className="flex items-center space-x-2">
                  <p>{product.description}</p>
                  <button
                    onClick={() => handleRemoveProduct(product.id)}
                    className="btn btn-outline btn-danger"
                  >
                    Remove
                  </button>
                  <button className="btn btn-outline">Preview</button>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default OrdersPage;
